import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import './App.css';

function App() {
  const [theme, setTheme] = useState('light');

  useEffect(() => {
    const sections = document.querySelectorAll('.fade-in-section');
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.1 }
    );
    sections.forEach((section) => observer.observe(section));
    return () => observer.disconnect();
  }, []);

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <div className={theme}>
      <Router>
        <Navbar toggleTheme={toggleTheme} theme={theme} />
        <Routes>
          <Route path="/" element={<Hero />} />
          <Route path="/about" element={<About />} />
          <Route path="/work" element={<WorkExperience />} />
          <Route path="/skills" element={<Skills />} />
          <Route path="/projects" element={<Projects />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </Router>
    </div>
  );
}

function Navbar({ toggleTheme, theme }) {
  return (
    <nav className="navbar navbar-expand-lg fixed-top shadow-lg bg-gray-900 dark:bg-gray-800">
      <div className="container mx-auto px-4">
        <Link className="navbar-brand font-bold text-2xl text-white" to="/">Erwin Sonido</Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto space-x-4">
            <li className="nav-item">
              <Link className="nav-link text-white hover:text-blue-400 transition-colors duration-200" to="/about">About</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white hover:text-blue-400 transition-colors duration-200" to="/work">Work</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white hover:text-blue-400 transition-colors duration-200" to="/skills">Skills</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white hover:text-blue-400 transition-colors duration-200" to="/projects">Projects</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link text-white hover:text-blue-400 transition-colors duration-200" to="/contact">Contact</Link>
            </li>
            <li className="nav-item">
              <button onClick={toggleTheme} className="text-white hover:text-blue-400 transition-colors duration-200">
                {theme === 'light' ? 'üåô Dark' : '‚òÄÔ∏è Light'}
              </button>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

function Hero() {
  return (
    <section className="hero fade-in-section min-h-screen flex items-center bg-gradient-to-r from-gray-800 to-blue-600 dark:from-gray-900 dark:to-blue-800">
      <div className="container mx-auto px-4">
        <div className="flex flex-col md:flex-row items-center">
          <div className="md:w-1/2 text-white p-6">
            <h1 className="text-4xl md:text-5xl font-bold mb-4 animate-slide-up">Hi, I'm Erwin Sonido</h1>
            <p className="text-lg mb-6 animate-slide-up animation-delay-200">
              A passionate web developer crafting innovative and user-friendly solutions with React and modern JavaScript.
            </p>
            <Link to="/projects" className="btn btn-primary btn-lg animate-slide-up animation-delay-400">Discover My Projects</Link>
          </div>
          <div className="md:w-1/2 h-[50vh] md:h-screen bg-cover bg-center hidden md:block hero-image"></div>
        </div>
      </div>
    </section>
  );
}

function About() {
  return (
    <section id="about" className="py-20 bg-gray-100 dark:bg-gray-900 fade-in-section">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-8 text-gray-900 dark:text-white">About Me</h2>
        <div className="flex flex-col items-center">
          <img
            src="/profile.jpg"
            alt="Profile"
            className="w-48 h-48 rounded-full mb-6 object-cover border-4 border-white shadow-lg transform transition hover:scale-105"
          />
          <p className="text-center max-w-2xl text-gray-700 dark:text-gray-300">
            I'm a dedicated web developer with expertise in React.js, Bootstrap, and modern JavaScript. My goal is to create seamless digital experiences that solve real-world problems.
          </p>
        </div>
      </div>
    </section>
  );
}

function WorkExperience() {
  const experiences = [
    {
      title: 'Frontend Developer',
      company: 'Tech Solutions Inc.',
      duration: 'Jan 2023 - Present',
      description: 'Developed responsive web applications using React and TypeScript, improving user engagement by 30%.',
    },
    {
      title: 'Junior Developer',
      company: 'WebWorks LLC',
      duration: 'Jun 2021 - Dec 2022',
      description: 'Built and maintained client websites with HTML, CSS, and JavaScript, focusing on performance optimization.',
    },
  ];

  return (
    <section id="work" className="py-20 fade-in-section bg-white dark:bg-gray-800">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-8 text-gray-900 dark:text-white">Work Experience</h2>
        <div className="max-w-3xl mx-auto">
          {experiences.map((exp, index) => (
            <div key={index} className="relative pl-10 mb-8 border-l-4 border-blue-500">
              <div className="absolute -left-2 top-2 w-4 h-4 bg-white border-2 border-blue-500 rounded-full"></div>
              <h3 className="text-xl font-semibold text-gray-900 dark:text-white">{exp.title}</h3>
              <p className="text-gray-600 dark:text-gray-400">{exp.company} | {exp.duration}</p>
              <p className="text-gray-700 dark:text-gray-300">{exp.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

function Skills() {
  const skills = [
    { name: 'React.js', level: 90 },
    { name: 'JavaScript', level: 85 },
    { name: 'Bootstrap', level: 80 },
    { name: 'HTML/CSS', level: 95 },
  ];

  return (
    <section id="skills" className="py-20 bg-gray-100 dark:bg-gray-900 fade-in-section">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-8 text-gray-900 dark:text-white">My Skills</h2>
        <div className="max-w-lg mx-auto">
          {skills.map((skill, index) => (
            <div key={index} className="mb-4">
              <h5 className="font-medium text-gray-900 dark:text-white">{skill.name}</h5>
              <div className="w-full bg-gray-200 rounded-full h-2.5 dark:bg-gray-700">
                <div
                  className="bg-blue-600 h-2.5 rounded-full transition-all duration-500"
                  style={{ width: `${skill.level}%` }}
                >
                </div>
              </div>
              <span className="text-sm text-gray-600 dark:text-gray-400">{skill.level}%</span>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

function Projects() {
  const projects = [
    {
      title: 'E-Commerce Platform',
      description: 'A full-stack e-commerce site with payment integration using React and Node.js.',
      link: '#',
      image: 'https://via.placeholder.com/300x200?text=Project+1',
    },
    {
      title: 'Portfolio Website',
      description: 'A responsive portfolio showcasing my skills, built with React and Bootstrap.',
      link: '#',
      image: 'https://via.placeholder.com/300x200?text=Project+2',
    },
    {
      title: 'Task Manager',
      description: 'A task management app with real-time updates using Firebase.',
      link: '#',
      image: 'https://via.placeholder.com/300x200?text=Project+3',
    },
  ];

  return (
    <section id="projects" className="py-20 fade-in-section bg-white dark:bg-gray-800">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-8 text-gray-900 dark:text-white">My Projects</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {projects.map((project, index) => (
            <div key={index} className="group rounded-lg shadow-lg overflow-hidden transform transition hover:-translate-y-2 hover:shadow-xl">
              <img src={project.image} className="w-full h-48 object-cover" alt={project.title} />
              <div className="p-4 bg-white dark:bg-gray-900">
                <h5 className="text-lg font-semibold text-gray-900 dark:text-white">{project.title}</h5>
                <p className="text-gray-600 dark:text-gray-400">{project.description}</p>
                <Link to={project.link} className="btn btn-primary mt-2">View Project</Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

function Contact() {
  const { register, handleSubmit, formState: { errors }, reset } = useForm();

  const onSubmit = (data) => {
    alert(`Form submitted!\nName: ${data.name}\nEmail: ${data.email}\nMessage: ${data.message}`);
    reset();
  };

  return (
    <section id="contact" className="py-20 bg-gray-100 dark:bg-gray-900 fade-in-section">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-8 text-gray-900 dark:text-white">Contact Me</h2>
        <div className="max-w-lg mx-auto">
          <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Name</label>
              <input
                type="text"
                className={`mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50 dark:bg-gray-800 dark:text-white ${errors.name ? 'border-red-500' : ''}`}
                {...register('name', { required: 'Name is required', minLength: { value: 2, message: 'Name must be at least 2 characters' } })}
              />
              {errors.name && <p className="text-red-500 text-sm mt-1">{errors.name.message}</p>}
            </div>
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Email</label>
              <input
                type="email"
                className={`mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50 dark:bg-gray-800 dark:text-white ${errors.email ? 'border-red-500' : ''}`}
                {...register('email', { required: 'Email is required', pattern: { value: /^\S+@\S+$/i, message: 'Invalid email address' } })}
              />
              {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email.message}</p>}
            </div>
            <div>
              <label htmlFor="message" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Message</label>
              <textarea
                className={`mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50 dark:bg-gray-800 dark:text-white ${errors.message ? 'border-red-500' : ''}`}
                rows="4"
                {...register('message', { required: 'Message is required', minLength: { value: 10, message: 'Message must be at least 10 characters' } })}
              ></textarea>
              {errors.message && <p className="text-red-500 text-sm mt-1">{errors.message.message}</p>}
            </div>
            <button type="submit" className="w-full btn btn-primary">Send Message</button>
          </form>
        </div>
      </div>
    </section>
  );
}

export default App;
